import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom

const HeroSection = () => {
  return (
    <div className="relative h-screen flex items-center justify-center text-center bg-gradient-to-r from-blue-600 to-blue-800">
      <motion.div
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
        className="relative z-10 text-white"
      >
        <h1 className="text-5xl md:text-6xl font-bold mb-4">Your Health, Our Priority</h1>
        <p className="text-lg md:text-xl mb-8">
          Get the best health insurance plans tailored to your needs.
        </p>
        {/* Link to the Query Form page */}
        <Link
          to="/query-form"
          className="bg-orange-500 text-white px-8 py-3 rounded-lg hover:bg-orange-600 transition duration-300"
        >
          Get Started
        </Link>
      </motion.div>
      <div className="absolute inset-0 bg-black bg-opacity-30"></div>
    </div>
  );
};

export default HeroSection;
